import React, {Component} from 'react';
import { Table,Header,HeaderRow,HeaderCell,Body,Row,Cell} from '@table-library/react-table-library/table';

class Create extends Component {
  render() {   
    return(
    <addTodo>       
       <form id="addTodoFrm" onSubmit={function (event) {
         event.preventDefault();
         this.props.addTodo(event.target.importance.value, event.target.title.value, event.target.task.value, event.target.deadline.value);
     }}>
       <h2>add todo</h2>
       <input type='text' name='importance' placeholder='Importance'></input><br/>
       <input type='text' name='title' placeholder='Title'></input><br/>
       <input type='text' name='task' placeholder='Task'></input><br/>
       <input type='text' name='deadline' placeholder='Deadline'></input><br/>
       <button type="submit">add</button>
       </form>
    </addTodo>
    );
  }
}

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      todo: [{
        importance: 'high',
        title : 'SPARCS',
        task: 'homework for newbies',
        deadline: "today",
      }],
    }

  };

  render() {
    console.log('working!'); 
    return (
      <>
      <Table data={{nodes : this.state.todo}}>{(tableList) => (
        <>
        <Header>
          <HeaderRow>
            <HeaderCell>Importance</HeaderCell>
            <HeaderCell>Title</HeaderCell>
            <HeaderCell>Task</HeaderCell>
            <HeaderCell>Deadline</HeaderCell>
          </HeaderRow>
        </Header>
        <Body>
        {tableList.map((item) => (
          <Row key={item.id} item={item}>
            <Cell>{item.importance}</Cell>
            <Cell>{item.title}</Cell>
            <Cell>{item.task}</Cell>
            <Cell>{item.deadline}</Cell>
          </Row>
        ))}
      </Body>
      </>
      )}</Table><br/>

    <Create addTodo={function (_importance, _title, _task, _deadline) {
        var todo = this.state.todo;
        var updated = todo.concat(
          {importance : _importance, title: _title, task: _task, deadline: _deadline}
        );
        this.setState({
          todo :updated
        });
      }.bind(this)}></Create>
    </>
    );
  }



};

export default App;
